import React, { useState } from 'react';
import '../styles/InputSection.css';
import TextInput from './TextInput';
import AudioInput from './AudioInput';
import ImageInput from './ImageInput';
import CombinedInput from './CombinedInput';

const InputSection = ({ 
  onTextAnalysis, 
  onAudioAnalysis, 
  onImageAnalysis, 
  onCombinedAnalysis, 
  loading 
}) => {
  const [activeTab, setActiveTab] = useState('text');

  const tabs = [
    { id: 'text', label: '📝 Text Analysis', icon: '📝' },
    { id: 'audio', label: '🎤 Audio Analysis', icon: '🎤' },
    { id: 'image', label: '📸 Image Analysis', icon: '📸' },
    { id: 'combined', label: '🔄 Combined Analysis', icon: '🔄' }
  ];

  const renderActiveComponent = () => {
    switch (activeTab) {
      case 'text':
        return <TextInput onAnalysis={onTextAnalysis} loading={loading} />;
      case 'audio':
        return <AudioInput onAnalysis={onAudioAnalysis} loading={loading} />;
      case 'image':
        return <ImageInput onAnalysis={onImageAnalysis} loading={loading} />;
      case 'combined':
        return <CombinedInput onAnalysis={onCombinedAnalysis} loading={loading} />;
      default:
        return <TextInput onAnalysis={onTextAnalysis} loading={loading} />;
    }
  };

  return (
    <section className="input-section">
      <div className="input-container">
        <h2 className="section-title">Choose Your Input Method</h2>
        
        <div className="tabs">
          {tabs.map(tab => (
            <button
              key={tab.id}
              className={`tab ${activeTab === tab.id ? 'active' : ''}`}
              onClick={() => setActiveTab(tab.id)}
              disabled={loading}
            >
              <span className="tab-icon">{tab.icon}</span>
              <span className="tab-label">{tab.label}</span>
            </button>
          ))}
        </div>

        <div className="tab-content">
          {renderActiveComponent()}
        </div>
      </div>
    </section>
  );
};

export default InputSection;
