import React, { useState, useEffect } from 'react';
import './styles/App.css';
import Header from './components/Header';
import InputSection from './components/InputSection';
import ResultsSection from './components/ResultsSection';
import LoadingSpinner from './components/LoadingSpinner';

function App() {
  const [results, setResults] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('text');

  const handleAnalysis = async (analysisData) => {
    setLoading(true);
    setError(null);
    setResults(null);

    try {
      const response = await fetch('/analyze/combined', {
        method: 'POST',
        body: analysisData
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      setResults(data);
    } catch (err) {
      setError(err.message);
      console.error('Analysis error:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleTextAnalysis = async (text, contentType, numRecommendations) => {
    setLoading(true);
    setError(null);
    setResults(null);

    try {
      const response = await fetch('/analyze/text', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          text,
          content_type: contentType,
          num_recommendations: numRecommendations
        })
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      setResults(data);
    } catch (err) {
      setError(err.message);
      console.error('Text analysis error:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleAudioAnalysis = async (audioFile, contentType, numRecommendations) => {
    setLoading(true);
    setError(null);
    setResults(null);

    try {
      const formData = new FormData();
      formData.append('audio_file', audioFile);
      formData.append('content_type', contentType);
      formData.append('num_recommendations', numRecommendations);

      const response = await fetch('/analyze/audio', {
        method: 'POST',
        body: formData
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      setResults(data);
    } catch (err) {
      setError(err.message);
      console.error('Audio analysis error:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleImageAnalysis = async (imageFile, contentType, numRecommendations) => {
    setLoading(true);
    setError(null);
    setResults(null);

    try {
      const formData = new FormData();
      formData.append('image_file', imageFile);
      formData.append('content_type', contentType);
      formData.append('num_recommendations', numRecommendations);

      const response = await fetch('/analyze/image', {
        method: 'POST',
        body: formData
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      setResults(data);
    } catch (err) {
      setError(err.message);
      console.error('Image analysis error:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <Header activeTab={activeTab} onChangeTab={setActiveTab} />
      
      <main className="main-content">
        <div className="container">
          <div className="hero-section">
            <div className="hero-content">
              <h1 className="hero-title">
                <span className="emoji">üé¨</span>
                AI-Powered Movie Recommendations
              </h1>
              <p className="hero-subtitle">
                Discover your perfect movie or TV show based on your emotions using cutting-edge AI analysis
              </p>
              <div className="hero-features">
                <div className="feature">
                  <span className="feature-icon">üìù</span>
                  <span>Text Analysis</span>
                </div>
                <div className="feature">
                  <span className="feature-icon">üé§</span>
                  <span>Voice Analysis</span>
                </div>
                <div className="feature">
                  <span className="feature-icon">üì∏</span>
                  <span>Face Analysis</span>
                </div>
                <div className="feature">
                  <span className="feature-icon">ü§ñ</span>
                  <span>AI Powered</span>
                </div>
              </div>
            </div>
          </div>

          <div className="tabs-container">
            <div className="tabs">
              <button 
                className={`tab ${activeTab === 'text' ? 'active' : ''}`}
                onClick={() => setActiveTab('text')}
              >
                <span className="tab-icon">üìù</span>
                Text Analysis
              </button>
              <button 
                className={`tab ${activeTab === 'audio' ? 'active' : ''}`}
                onClick={() => setActiveTab('audio')}
              >
                <span className="tab-icon">üé§</span>
                Voice Analysis
              </button>
              <button 
                className={`tab ${activeTab === 'image' ? 'active' : ''}`}
                onClick={() => setActiveTab('image')}
              >
                <span className="tab-icon">üì∏</span>
                Face Analysis
              </button>
              <button 
                className={`tab ${activeTab === 'combined' ? 'active' : ''}`}
                onClick={() => setActiveTab('combined')}
              >
                <span className="tab-icon">üîó</span>
                Combined
              </button>
            </div>
          </div>

          <InputSection 
            activeTab={activeTab}
            onTextAnalysis={handleTextAnalysis}
            onAudioAnalysis={handleAudioAnalysis}
            onImageAnalysis={handleImageAnalysis}
            onCombinedAnalysis={handleAnalysis}
            loading={loading}
          />

          {loading && <LoadingSpinner />}

          {error && (
            <div className="error-message">
              <div className="error-icon">‚ö†Ô∏è</div>
              <div className="error-content">
                <h3>Oops! Something went wrong</h3>
                <p>{error}</p>
              </div>
            </div>
          )}

          {results && <ResultsSection results={results} />}
        </div>
      </main>

      <footer className="footer">
        <div className="footer-content">
          <p>&copy; 2024 OTT Recommendation System. Powered by Hugging Face AI Models.</p>
          <div className="footer-links">
            <a href="#about">About</a>
            <a href="#privacy">Privacy</a>
            <a href="#contact">Contact</a>
          </div>
        </div>
      </footer>
    </div>
  );
}

export default App;
