import React, { useState } from 'react';
import '../styles/CombinedInput.css';

const CombinedInput = ({ onAnalysis, loading }) => {
  const [text, setText] = useState('');
  const [audioFile, setAudioFile] = useState(null);
  const [imageFile, setImageFile] = useState(null);
  const [contentType, setContentType] = useState('movie');
  const [numRecommendations, setNumRecommendations] = useState(10);

  const handleFileChange = (type, e) => {
    const file = e.target.files[0];
    if (type === 'audio' && file && file.type.startsWith('audio/')) {
      setAudioFile(file);
    } else if (type === 'image' && file && file.type.startsWith('image/')) {
      setImageFile(file);
    } else {
      alert(`Please select a valid ${type} file`);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    // Check if at least one input is provided
    if (!text.trim() && !audioFile && !imageFile) {
      alert('Please provide at least one input (text, audio, or image)');
      return;
    }

    const formData = new FormData();
    
    if (text.trim()) {
      formData.append('text', text.trim());
    }
    
    if (audioFile) {
      formData.append('audio_file', audioFile);
    }
    
    if (imageFile) {
      formData.append('image_file', imageFile);
    }
    
    formData.append('content_type', contentType);
    formData.append('num_recommendations', numRecommendations);

    onAnalysis(formData);
  };

  const clearInput = (type) => {
    switch (type) {
      case 'text':
        setText('');
        break;
      case 'audio':
        setAudioFile(null);
        break;
      case 'image':
        setImageFile(null);
        break;
      default:
        break;
    }
  };

  const clearAll = () => {
    setText('');
    setAudioFile(null);
    setImageFile(null);
  };

  return (
    <div className="combined-input">
      <div className="input-header">
        <h3>üîÑ Combined Emotion Analysis</h3>
        <p>Use multiple inputs for more accurate emotion detection and better recommendations</p>
      </div>

      <form onSubmit={handleSubmit} className="combined-form">
        <div className="input-grid">
          {/* Text Input */}
          <div className="input-card">
            <div className="card-header">
              <h4>üìù Text Input</h4>
              <button 
                type="button" 
                onClick={() => clearInput('text')}
                className="clear-btn"
                disabled={loading}
              >
                ‚úï
              </button>
            </div>
            <textarea
              value={text}
              onChange={(e) => setText(e.target.value)}
              placeholder="Describe how you're feeling..."
              className="text-input"
              rows="3"
              disabled={loading}
            />
          </div>

          {/* Audio Input */}
          <div className="input-card">
            <div className="card-header">
              <h4>üé§ Audio Input</h4>
              <button 
                type="button" 
                onClick={() => clearInput('audio')}
                className="clear-btn"
                disabled={loading}
              >
                ‚úï
              </button>
            </div>
            <div className="file-upload">
              <input
                type="file"
                accept="audio/*"
                onChange={(e) => handleFileChange('audio', e)}
                className="file-input"
                id="combined-audio"
                disabled={loading}
              />
              <label htmlFor="combined-audio" className="file-label">
                Choose Audio File
              </label>
              {audioFile && (
                <div className="file-info">
                  <span>üéµ {audioFile.name}</span>
                </div>
              )}
            </div>
          </div>

          {/* Image Input */}
          <div className="input-card">
            <div className="card-header">
              <h4>üì∏ Image Input</h4>
              <button 
                type="button" 
                onClick={() => clearInput('image')}
                className="clear-btn"
                disabled={loading}
              >
                ‚úï
              </button>
            </div>
            <div className="file-upload">
              <input
                type="file"
                accept="image/*"
                onChange={(e) => handleFileChange('image', e)}
                className="file-input"
                id="combined-image"
                disabled={loading}
              />
              <label htmlFor="combined-image" className="file-label">
                Choose Image File
              </label>
              {imageFile && (
                <div className="file-info">
                  <img 
                    src={URL.createObjectURL(imageFile)} 
                    alt="Preview" 
                    className="image-preview"
                  />
                  <span>üì∑ {imageFile.name}</span>
                </div>
              )}
            </div>
          </div>
        </div>

        <div className="form-row">
          <div className="form-group">
            <label htmlFor="content-type" className="form-label">
              Content Type
            </label>
            <select
              id="content-type"
              value={contentType}
              onChange={(e) => setContentType(e.target.value)}
              className="form-select"
              disabled={loading}
            >
              <option value="movie">Movies</option>
              <option value="tv_series">TV Series</option>
            </select>
          </div>

          <div className="form-group">
            <label htmlFor="num-recommendations" className="form-label">
              Number of Recommendations
            </label>
            <select
              id="num-recommendations"
              value={numRecommendations}
              onChange={(e) => setNumRecommendations(parseInt(e.target.value))}
              className="form-select"
              disabled={loading}
            >
              <option value={5}>5</option>
              <option value={10}>10</option>
              <option value={15}>15</option>
              <option value={20}>20</option>
            </select>
          </div>
        </div>

        <div className="form-actions">
          <button 
            type="button" 
            onClick={clearAll}
            className="clear-all-btn"
            disabled={loading}
          >
            üóëÔ∏è Clear All
          </button>
          
          <button 
            type="submit" 
            className="submit-btn"
            disabled={loading || (!text.trim() && !audioFile && !imageFile)}
          >
            {loading ? 'Analyzing...' : 'Analyze All & Get Recommendations'}
          </button>
        </div>
      </form>

      <div className="info-box">
        <h4>‚ÑπÔ∏è Combined Analysis Benefits:</h4>
        <ul>
          <li>More accurate emotion detection using multiple modalities</li>
          <li>Better recommendations based on comprehensive analysis</li>
          <li>You can use any combination of text, audio, and image inputs</li>
          <li>Our AI combines all inputs for the most accurate results</li>
        </ul>
      </div>
    </div>
  );
};

export default CombinedInput;
