import React, { useState } from 'react';
import '../styles/TextInput.css';

const TextInput = ({ onAnalysis, loading }) => {
  const [text, setText] = useState('');
  const [contentType, setContentType] = useState('movie');
  const [numRecommendations, setNumRecommendations] = useState(10);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (text.trim()) {
      onAnalysis(text.trim(), contentType, numRecommendations);
    }
  };

  const sampleTexts = [
    "I'm feeling really sad and lonely today",
    "I'm so excited and happy! This is amazing!",
    "I'm really angry about what happened",
    "I'm scared and worried about the future",
    "I feel neutral and calm today",
    "I'm feeling lazy and unmotivated"
  ];

  const handleSampleClick = (sampleText) => {
    setText(sampleText);
  };

  return (
    <div className="text-input">
      <div className="input-header">
        <h3>üìù Text Emotion Analysis</h3>
        <p>Describe how you're feeling and get personalized movie recommendations</p>
      </div>

      <form onSubmit={handleSubmit} className="text-form">
        <div className="form-group">
          <label htmlFor="text-input" className="form-label">
            How are you feeling?
          </label>
          <textarea
            id="text-input"
            value={text}
            onChange={(e) => setText(e.target.value)}
            placeholder="Describe your current mood or feelings..."
            className="text-area"
            rows="4"
            disabled={loading}
            required
          />
        </div>

        <div className="form-row">
          <div className="form-group">
            <label htmlFor="content-type" className="form-label">
              Content Type
            </label>
            <select
              id="content-type"
              value={contentType}
              onChange={(e) => setContentType(e.target.value)}
              className="form-select"
              disabled={loading}
            >
              <option value="movie">Movies</option>
              <option value="tv_series">TV Series</option>
            </select>
          </div>

          <div className="form-group">
            <label htmlFor="num-recommendations" className="form-label">
              Number of Recommendations
            </label>
            <select
              id="num-recommendations"
              value={numRecommendations}
              onChange={(e) => setNumRecommendations(parseInt(e.target.value))}
              className="form-select"
              disabled={loading}
            >
              <option value={5}>5</option>
              <option value={10}>10</option>
              <option value={15}>15</option>
              <option value={20}>20</option>
            </select>
          </div>
        </div>

        <button 
          type="submit" 
          className="submit-btn"
          disabled={loading || !text.trim()}
        >
          {loading ? 'Analyzing...' : 'Analyze Emotion & Get Recommendations'}
        </button>
      </form>

      <div className="sample-texts">
        <h4>üí° Sample Texts (Click to try):</h4>
        <div className="sample-grid">
          {sampleTexts.map((sample, index) => (
            <button
              key={index}
              className="sample-btn"
              onClick={() => handleSampleClick(sample)}
              disabled={loading}
            >
              {sample}
            </button>
          ))}
        </div>
      </div>

      <div className="info-box">
        <h4>‚ÑπÔ∏è How it works:</h4>
        <ul>
          <li>Our AI analyzes your text to detect emotions like sadness, joy, anger, fear, etc.</li>
          <li>Based on your detected emotion, we recommend movies that match your mood</li>
          <li>We use advanced Hugging Face models for accurate emotion detection</li>
        </ul>
      </div>
    </div>
  );
};

export default TextInput;
