import React, { useState, useRef } from 'react';
import '../styles/AudioInput.css';

const AudioInput = ({ onAnalysis, loading }) => {
  const [audioFile, setAudioFile] = useState(null);
  const [contentType, setContentType] = useState('movie');
  const [numRecommendations, setNumRecommendations] = useState(10);
  const [isRecording, setIsRecording] = useState(false);
  const [recordedAudio, setRecordedAudio] = useState(null);
  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file && file.type.startsWith('audio/')) {
      setAudioFile(file);
      setRecordedAudio(null);
    } else {
      alert('Please select a valid audio file');
    }
  };

  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      mediaRecorderRef.current = new MediaRecorder(stream);
      audioChunksRef.current = [];

      mediaRecorderRef.current.ondataavailable = (event) => {
        audioChunksRef.current.push(event.data);
      };

      mediaRecorderRef.current.onstop = () => {
        const inferredType = (audioChunksRef.current[0] && audioChunksRef.current[0].type) || (mediaRecorderRef.current && mediaRecorderRef.current.mimeType) || 'audio/webm';
        const audioBlob = new Blob(audioChunksRef.current, { type: inferredType });
        setRecordedAudio(audioBlob);
        setAudioFile(null);
        stream.getTracks().forEach(track => track.stop());
      };

      mediaRecorderRef.current.start();
      setIsRecording(true);
    } catch (error) {
      console.error('Error accessing microphone:', error);
      alert('Error accessing microphone. Please check permissions.');
    }
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current && isRecording) {
      mediaRecorderRef.current.stop();
      setIsRecording(false);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const audioToAnalyze = recordedAudio || audioFile;
    if (audioToAnalyze) {
      onAnalysis(audioToAnalyze, contentType, numRecommendations);
    }
  };

  const clearAudio = () => {
    setAudioFile(null);
    setRecordedAudio(null);
  };

  return (
    <div className="audio-input">
      <div className="input-header">
        <h3>🎤 Audio Emotion Analysis</h3>
        <p>Record your voice or upload an audio file to analyze your emotions</p>
      </div>

      <form onSubmit={handleSubmit} className="audio-form">
        <div className="audio-options">
          <div className="audio-option">
            <h4>📁 Upload Audio File</h4>
            <div className="file-upload">
              <input
                type="file"
                accept="audio/*"
                onChange={handleFileChange}
                className="file-input"
                id="audio-file"
                disabled={loading || isRecording}
              />
              <label htmlFor="audio-file" className="file-label">
                Choose Audio File
              </label>
              {audioFile && (
                <div className="file-info">
                  <span>📄 {audioFile.name}</span>
                  <audio
                    controls
                    src={URL.createObjectURL(audioFile)}
                    className="audio-preview"
                  >
                    Your browser does not support the audio element.
                  </audio>
                  <div style={{display:'flex', gap:'0.5rem'}}>
                    <button type="button" onClick={clearAudio} className="clear-btn">
                      ✕
                    </button>
                    <button
                      type="button"
                      className="analyze-inline-btn"
                      onClick={() => onAnalysis(audioFile, contentType, numRecommendations)}
                      disabled={loading}
                    >
                      Analyze this audio
                    </button>
                  </div>
                </div>
              )}
            </div>
          </div>

          <div className="divider">OR</div>

          <div className="audio-option">
            <h4>🎙️ Record Audio</h4>
            <div className="recording-controls">
              {!isRecording ? (
                <button
                  type="button"
                  onClick={startRecording}
                  className="record-btn"
                  disabled={loading}
                >
                  🎙️ Start Recording
                </button>
              ) : (
                <button
                  type="button"
                  onClick={stopRecording}
                  className="stop-btn"
                >
                  ⏹️ Stop Recording
                </button>
              )}
              
              {recordedAudio && (
                <div className="recording-info">
                  <span>🎵 Recording completed</span>
                  <audio controls src={URL.createObjectURL(recordedAudio)} className="audio-preview">
                    Your browser does not support the audio element.
                  </audio>
                  <div style={{display:'flex', gap:'0.5rem', justifyContent:'center'}}>
                    <button type="button" onClick={clearAudio} className="clear-btn">
                      ✕ Clear
                    </button>
                    <button
                      type="button"
                      className="analyze-inline-btn"
                      onClick={() => onAnalysis(recordedAudio, contentType, numRecommendations)}
                      disabled={loading}
                    >
                      Analyze this recording
                    </button>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>

        <div className="form-row">
          <div className="form-group">
            <label htmlFor="content-type" className="form-label">
              Content Type
            </label>
            <select
              id="content-type"
              value={contentType}
              onChange={(e) => setContentType(e.target.value)}
              className="form-select"
              disabled={loading}
            >
              <option value="movie">Movies</option>
              <option value="tv_series">TV Series</option>
            </select>
          </div>

          <div className="form-group">
            <label htmlFor="num-recommendations" className="form-label">
              Number of Recommendations
            </label>
            <select
              id="num-recommendations"
              value={numRecommendations}
              onChange={(e) => setNumRecommendations(parseInt(e.target.value))}
              className="form-select"
              disabled={loading}
            >
              <option value={5}>5</option>
              <option value={10}>10</option>
              <option value={15}>15</option>
              <option value={20}>20</option>
            </select>
          </div>
        </div>

        <button 
          type="submit" 
          className="submit-btn"
          disabled={loading || (!audioFile && !recordedAudio)}
        >
          {loading ? 'Analyzing Audio...' : 'Analyze Emotion & Get Recommendations'}
        </button>
      </form>

      <div className="info-box">
        <h4>ℹ️ Audio Analysis Tips:</h4>
        <ul>
          <li>Speak clearly and express your emotions naturally</li>
          <li>Supported formats: WAV, MP3, M4A</li>
          <li>Recording should be 3-10 seconds for best results</li>
          <li>Our AI analyzes speech patterns, tone, and emotional cues</li>
        </ul>
      </div>
    </div>
  );
};

export default AudioInput;
